    private void jButtonAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAddActionPerformed
        
        float num1, num2, result;
        
        num1 = Float.parseFloat(jTextFieldFirstNum.getText());
        num2 = Float.parseFloat(jTextFieldSecNum.getText());
       
        result = num1+num2;
        jTextFieldResult.setText(String.valueOf(result));

    }//GEN-LAST:event_jButtonAddActionPerformed

    private void jToggleButtonSubsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jToggleButtonSubsActionPerformed
 
        float num1, num2, result;
      
        num1 = Float.parseFloat(jTextFieldFirstNum.getText());
        num2 = Float.parseFloat(jTextFieldSecNum.getText());
     
        result = num1-num2;
        jTextFieldResult.setText(String.valueOf(result));
    }//GEN-LAST:event_jToggleButtonSubsActionPerformed

