    private void calculate(char operator){
        
        try {
        float num1, num2, result = 0;
        num1 = Float.parseFloat(jTextFieldFirstNum.getText());
        num2 = Float.parseFloat(jTextFieldSecNum.getText());
        
        switch (operator){
            case '+' : result = num1 + num2;
            break;
            case '-' : result = num1 - num2;
            break;
            case '*' : result = num1 * num2;
            break;
            case '/' : result = num1 / num2;
            break;
           
        }
        
        jTextFieldResult.setText(String.valueOf(result));
    
    }catch (NumberFormatException e) {
      System.out.println("You have to enter number only!");
    }
    }
    
    
    
